import React, {Component} from "react"
import styles from "./LayoutContainer.module.css"
import Toolbar from "./../ToolbarContainer/ToolbarContainer"
import Aux from "./../../hocs/Aux/Aux"
import SignUpForm from "./../formsContainers/SignupFormContainer"
import LoginForm from "./../formsContainers/LoginFormContainer"

class LayoutContainer extends Component{
  constructor(props){
    super(props);
    this.state = {
      isAuthenticated:{
        status: false,
        data: null
      },
      formType:"login"
    }
  }
  changeFormTypeHandler = (value)=>{
    this.setState({
      formType: value
    })
  }
  render(){
    return(
      <div className = {styles.LayoutContainer}>
        <Toolbar changeFormTypeHandler = {this.changeFormTypeHandler}/>
        <main>
          {
            this.state.formType == "signup"?<SignUpForm/>:<LoginForm/>
          }
        </main>
      </div>
    )
  }
}

export default LayoutContainer;
