import React, {Component} from "react";
import Button from "./../UI/Button/Button";
import styles from "./SignupFormContainer.module.css";
import axios from "axios";
import Input from "./../UI/Input/Input";

class SignupFormContainer extends Component{
  constructor(props){
    super(props)
    this.state = {
      form:{
        name: {
          elementType: "input",
          inputType: "text",
          placeholder: "Your Name",
          value: "",
          validationRules:{
            required: true
          },
          valid: false,//whether value is false or correct
          touched: false
        },
        enrollNo:{
          elementType: "input",
          inputType: "number",
          placeholder: "17103046",
          valid: "false",
          validationRules:{
            required: true,
            isNumber: true
          }
        },
        email:{
          elementType: "input",
          inputType: "email",
          placeholder: "Your Email",
          value:" ",
          valid: false,
          validationRules:{
            required: true,
            isEmail: true
          }
        },
        password:{
          elementType: "input",
          inputType: "password",
          placeholder: "Password",
          value: "",
          valid: false,
          validationRules:{
            required: true,
            isPassword: true
          }
        }
      },
      formIsValid: false,//this property can be used to check whether we can submit the form or not
      loading: false
    }
  }

  render(){
    const form = Object.keys(this.state.form).map(inputField => (
      <Input elementType = {this.state.form[inputField].elementType}
      inputType = {this.state.form[inputField].inputType}
      placeholder = {this.state.form[inputField].placeholder}
      value = {this.state.form[inputField].val}
      valid = {this.state.form[inputField].valid}/>
    ))
    return(
      <form onSubmit = {this.signupHandler} className = {styles.SignupFormContainer}>
        {form}
        <Button disabled = {!this.state.formIsValid}>Submit</Button>
      </form>
    )
  }
}

export default SignupFormContainer;
